emp_addr_detail = #{empAddrDetail}, emp_post = #{empPost}, emp_phone = #{empPhone}, emp_image = #{empImage},
emp_store_image = #{empStoreImage}, emp_hire_date = #{empHireDate}, emp_email = #{empEmail} where emp_num = #{empNum}
